INSERT INTO `sys_book` VALUES ('1000', '1100', 'F014.3/D223', '流通与营销理论发展研究', '流通经济学-研究', '30多年来，“商贸流通”栏目伴随着我国社会主义经济不断深入改革和扩大开放，不仅走过了从计划经济到市场经济理论与实践的研究与探索过程，见证了我国商贸流通发生翻天覆地变化的历史，也突破了原来狭隘地单纯谈论国内贸易，就商业论商业的局限性。为了展示“贸易经济”栏目30多年的优秀成果，学报编辑部决定出版一套“贸易经济”丛书，第�', 2017, '邓艳', '978-7-5141-7781-7', '', 'F014.3', '', '经济科学出版社', '2019-01-04 17:58:19', '', NULL, '1209846', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
