INSERT INTO `sys_book` VALUES ('1003', '1103', 'C93-02/W199', '《易经》管理密码', '管理哲学,管理学-周易-应用', '本书共分为灵修有道、心润有法、身强有术三篇,主要内容包括:鼎新者创、善变者立、能待者强、持和者明、正义者聚、知艰者全、知畜者乐、谦和者夷、正否者泰等。', 2016, '王建成', '978-7-5439-7058-8', '', 'C93-02,C93', '', '上海科学技术文献出版社', '2019-01-04 17:58:19', '', NULL, '1147604', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1004', '1104', 'F272.9/Z233b', '阿米巴经营之组织进化', '企业管理-组织管理学', '本书讲述了什么是高利润的阿米巴经营模式，并结合实战案例传授企业幸福工作的心法，为中国企业展现阿米巴经营的魅力。全书共分九章，内容包括：阿米巴之组织划分、阿米巴之会计系统的构筑、阿米巴之量化分权等。', 2015, '詹承坤', '978-7-5164-1084-4', '', 'F272.9', '', '企业管理出版社', '2019-01-04 17:58:19', '', NULL, '1071501', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1005', '1105', 'R194/H488', '安徽省公务员健康管理研究', '公务员-健康-卫生管理学-研究-安徽', '全书共7章,分别为:安徽省公务员健康研究依据,研究内容及解决的关键问题,研究思路及方案,安徽省公务员健康状态测量工具的研制,安徽省公务员健康现况及特征表现,安徽省公务员非健康致因,安徽省公务员健康管理操作。', 2016, '胡好', '978-7-5664-0926-3', '', 'R194,D630.3', '', '安徽大学出版社', '2019-01-04 17:58:19', '', NULL, '1160516', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1006', '1106', 'X92/T343', '安全管理学', '安全管理学-高等学校-教材', '本书是高等教育安全工程系列“十一五”规划教材之一。全书共14章，内容包括：绪论，安全管理学理论基础，安全文化，安全管理方法，安全目标管理，系统安全管理，安全生产法规，职业安全健康管理体系，安全信息管理，事故统计及分析，事故调查与处理，事故预防与控制，灾害事件与事故应急管理，现代安全管理方法的新发展。', 2009, '田水承,景国勋', '978-7-111-27694-4', '', 'X92-43', '', '机械工业出版社', '2019-01-04 17:58:19', '', NULL, '1204198', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1007', '1107', 'C93-49/C544', '奥巴马的管理之道', '管理学-通俗读物', '奥巴马是一位出色的领袖，本书通过了解奥巴马走向成功的关键点，对自己如何走出职业困境，如何达到职业生涯巅峰，如何更成功地做人做事，有更实际的认识。', 2016, '崔智东', '978-7-5168-0762-0', '', 'C93-49', '', '台海出版社', '2019-01-04 17:58:19', '', NULL, '1124180', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1008', '1108', 'C93-49/L597', '把一招练成绝招:当管理学遇上文艺复兴大师', '管理学-通俗读物', '本书作者在2012年的佛罗伦萨文化参访之行中,透过对许多伟大艺术品及其时代精神的思辨,而得出现代企业的五大精进之道:向乔托学“精深”,向达·芬奇学“精微”,向米开朗基罗学“精壮”,向拉斐尔学“精密”,向伽利略学“精准”。', 2017, '刘顺仁', '978-7-5699-1164-0', '', 'C93-49', '', '北京时代华文书局', '2019-01-04 17:58:19', '', NULL, '1192971', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1009', '1109', 'G424.21/L516', '班级教育管理通论', '班级-教育管理学', '本书阐发了班级教育管理的基本原理，探讨了有效地进行操作的方法和艺术，征引有借鉴价值的案例，并进行了点评。', 2008, '林冬桂', '978-7-5361-3623-6', '', 'G424.21', '', '广东高等教育出版社', '2019-01-04 17:58:19', '', NULL, '1218089', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('101', '201', 'null', '单片微型计算机原理及应用', '单片微型计算机', '全书共包括9个章节：第一章讲述微型计算机基础知识；第二章讲述MCS—51单片机硬件系统；第三章讲述MCS—51单片机指令系统与汇编程序设计；第四章讲述MCS—51单片机的C51程序设计；第五章讲述仿真软件Proteus和C51编译软件Keil进行单片机系统仿真的基本方法；第六章讲述通用I/O的应用；第七章讲述MCS—51单片机内部功能部件的工作原理及应用（含中断管理', 2018, '', '978-7-5682-5240-9', '', 'TP368.1', '', '北京理工大学出版社', '2019-01-04 17:57:49', '', NULL, '1265556', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
