INSERT INTO `sys_book` VALUES ('1012', '1112', 'C93/W215', '北大管理课精义', '管理学', '本书根据北大经济学课程编撰。以生动活泼的语言,从各个角度将北大管理学课程的主要内容通俗地展现在读者面前。详细介绍了关于领导、决策、组织管理、人力资源管理等方面内容,让读者读懂职场,学会管理,并通过这些知识解决工作中常见的一些问题。', 2014, '王平辉', '978-7-229-07317-6', '', 'C93', '', '重庆出版社', '2019-01-04 17:58:19', '', NULL, '974127', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
