INSERT INTO `sys_book` VALUES ('1033', '1133', 'C936/G284b', '创新的团队', '组织管理学-研究', '本书通过一个吸引人的商业故事,展示了人们潜在的工作风格偏好对团队和工作结果的影响。通过展现培养成功团队的突破性思维过程,作者介绍了以FourSight为基础的独特有效的工具,可以帮助从专业人士到职场新人提高解决问题的能力。', 2013, '格里瓦斯,普乔,许宗瑞,刘晓阳', '978-7-5123-4668-0', '', 'C936', '', '中国电力出版社', '2019-01-04 17:58:20', '', NULL, '937892', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
