INSERT INTO `sys_book` VALUES ('1065', '1165', 'C93/F144', '德鲁克的管理智慧', '管理学', '本书收集德鲁克著作中的精华思想,并对其所言进行了深刻地分析;同时理论联系实际,结合世界及中国的大中型企业的发展论证德鲁克思想在中国企业界的应用。', 2013, '凡禹', '978-7-121-16616-7', '', 'C93', '', '电子工业出版社', '2019-01-04 17:58:21', '', NULL, '939251', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
