INSERT INTO `sys_book` VALUES ('1080', '1180', 'C936/Z725', '动态匹配:组织结构理论·实证·案例', '组织管理学', '本书从范式的角度对关于组织结构的所有理论进行回顾,建立了一个背景因素与组织结构匹配的集成研究框架。通过对1946年以来关于组织结构维度的回顾,把文献中出现的13个组织结构维度,按照理论归纳的方法,分为四大类:复杂性、规范化、权力分配、协调机制。对这四个维度,设计相应的量表,通过探索性因子分析和验证型因子分析,', 2015, '朱晓武', '978-7-5096-3669-5', '', 'C936', '', '经济管理出版社', '2019-01-04 17:58:21', '', NULL, '1056811', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
