INSERT INTO `sys_book` VALUES ('1093', '1193', 'C936/S396', '复合型视角下组织支持形成及作用研究', '组织管理学-研究', '本书通过对国内外组织支持理论及研究现状的系统分析,深入解构了组织支持的含义和结构,并在组织和员工这两种不同视角下分别探讨员工视角的组织支持、组织视角的组织支持及其操作定义与测量方法。在此基础上,本书构建了一个员工-组织复合型视角的组织支持形成及作用研究框架,探讨人力资源管理通过组织支持对员工反应产生作用的根源和机理。', 2016, '邵芳', '978-7-5606-3940-6', '', 'C936', '', '西安电子科技大学出版社', '2019-01-04 17:58:22', '', NULL, '1108129', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1094', '1194', 'C931.9/H3172', '复杂网络在管理领域的应用研究', '管理学-互联网络-应用-研究', '本书共分集群演进和创新扩散上、下两篇。主要内容包括:集群基本问题界定;集群文献综述;集群演进,一个复杂适应性系统的自组织进程等。', 2013, '何铮,张晓军', '978-7-5647-1435-2', '', 'C931.9', '', '电子科技大学出版社', '2019-01-04 17:58:22', '', NULL, '911559', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1095', '1195', 'R17/D4422', '妇幼卫生管理学', '妇幼卫生-卫生管理学-医学院校-教材', '本书分17章，主要包括绪论、管理学的理论基础、妇幼卫生事业的发展、妇幼卫生法制建设与管理、妇幼卫生计划管理、妇幼卫生组织管理、妇幼卫生人力资源管理、妇幼卫生信息管理等内容。', 2014, '杜玉开,刘毅', '978-7-117-19426-6', '', 'R17-43', '', '人民卫生出版社', '2019-01-04 17:58:22', '', NULL, '1013236', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1096', '1196', 'F272.9/Z675a', '服从力', '企业管理-组织管理学', '本书内容：服从力这本书，周平老师和多位作者已构思多年。看到非常多的人，因为服从力不够而在岗位中处处受挫;看到非常多优秀的人，因为言语上的不当而得不到重用;看到很多人做了非常非常多的工作，却因为说出了自己与领导观念相反的言论，其工作业绩就由此被否定。综上所述，周平老师和多位作者及朋友们觉得不能再推迟了，必须让更多的人', 2016, '周平,蔡锦,何沅俸,高先林', '978-7-300-22908-9', '', 'F272.9', '', '中国人民大学出版社', '2019-01-04 17:58:22', '', NULL, '1142530', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1097', '1197', 'C93/F327a', '福列特论管理', '管理学-研究', '本书分论组织、论领导、论心理、论个体、论未来五部分。内容包括：企业是一个整合的统一体；建设性冲突；命令的下达；权力；最终职权的构想；职工代表制对重新塑造企业管理者公认类型的影响；论领导者与专家；领导理论与实践的偏差；工业护士的领导机遇；师生关系；控制心理学；认同与参与的心理特征等。', 2013, '福利特,吴晓波,郭京京,詹也', '978-7-111-42775-9', '', 'C93', '', '机械工业出版社', '2019-01-04 17:58:22', '', NULL, '934810', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1098', '1198', 'null', '改变与转变', '管理学', '本书以东西方文化的碰撞与管理哲学的思想汇集为索引，吸纳了中国《易经》变化的思想与西方管理哲学思维模式，融合而成改变与转变的方法与路径，在研究思想变化的过程中建立模型，给企业，尤其是为决策者在适应全球化带来的挑战时提供启发与指导。', 2014, '武世伟,王红波', '978-7-5162-0524-2', '', 'C93', '', '中国民主法制出版社', '2019-01-04 17:58:22', '', NULL, '987094', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1099', '1199', 'C93-53/Y221', '改革与创新:管理学问题研究', '管理学-研究-文集', '本书是2012年黑龙江大学主办的黑龙江省工商管理学科创新与发展研讨会优秀论文荟萃，这些论文从不同角度探讨了工商管理学科的发展如何践行科学发展观，同时深入探讨了本学科科学研究，教学实践创新和发展的策略、原则与路径以及如何拓宽视野。', 2012, '杨茁,宋国学,吴正刚', '978-7-81129-587-0', '', 'C93-53', '', '黑龙江大学出版社', '2019-01-04 17:58:22', '', NULL, '909088', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
