INSERT INTO `sys_book` VALUES ('1152', '1252', 'C93/Z274c', '管理的精髓:西方当代管理学名著导读', '管理学-名著-介绍-西方国家', '本书精选了近40部近十年来产生过较大影响力的西方管理学名著,如《重新定义公司》《领导力密码》《管理的真相》等,通过对其主要内容进行详细的梳理,帮助读者用最节约的时间掌握或了解这些著作的观点与理论特点。是一部非常实用的管理学读物。', 2017, '张力军', '978-7-5164-1473-6', '', 'C93', '', '企业管理出版社', '2019-01-04 17:58:24', '', NULL, '1189612', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1153', '1253', 'C93-53/G326', '管理的历史维度', '管理学-文集', '本书为作者近年来的管理随笔集,从历史包括军事和战争史的维度,对管理中的核心主题包括大国国家发展、战略决策与领导力、团队打造、领导力等进行重新审视,揭示影响组织兴衰治乱的深层因素,从而为管理者思考管理问题提供历史的借鉴,为管理学的研究与教学提供纵深感和历史感。', 2016, '宫玉振', '978-7-301-27572-6', '', 'C93-53', '', '北京大学出版社', '2019-01-04 17:58:24', '', NULL, '1163284', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
