INSERT INTO `sys_book` VALUES ('1179', '1279', 'null', '管理建模与仿真', '管理学-系统仿真', '本书将管理科学问题进行分类总结，在阐述系统建模、系统仿真等的概念内涵、基本理论、基本方法与技术的基础上，重点围绕基于MATLAB和NetLogo两种工具软件管理科学问题的建模与仿真，结合实例进行了详细的讲解与分析。', 2018, '姜金贵', '978-7-111-58609-8', '', 'C931', '', '机械工业出版社', '2019-01-04 17:58:25', '', NULL, '1250848', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('118', '218', 'null', '电子线路设计仿真与实例:orCAD与ProtelDXP', '电子电路－计算机辅助设计－应用软件', '本书从应用角度出发，结合电路设计中的实际问题，系统地介绍了CAD软件的基本功能和应用技巧，并结合典型应用实例说明电路系统设计的一般步骤和方法。每章末都附有小结和习题，习题中精选了电路分析、电子线路设计基础、模拟电子技术等课程中的一些典型电路设计的例子。', 2017, '', '978-7-5606-4716-6', '', 'TN702', '', '西安电子科技大学出版社', '2019-01-04 17:57:49', '', NULL, '1254084', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
