INSERT INTO `sys_book` VALUES ('1196', '1296', 'G642.477/J539', '管理类论文写作概论', '管理学-毕业论文-写作-高等学校-教学参考资料', '当前，管理学的研究正呈现方兴未艾之势。科学研究的成果最终要以学术论文的形式来体现，掌握论文写作方面的知识和方法是当代大学生必备的基本功。本书着重探讨了管理类论文写作的基本知识、方法和技巧，旨在提高大学生的科研能力，提升其写作水平。', 2017, '靳娟', '978-7-5635-5105-7', '', 'G642.477', '', '北京邮电大学出版社', '2019-01-04 17:58:25', '', NULL, '1227366', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1197', '1297', 'G642.477/L322', '管理类专业学位论文研究及撰写:MBAMPAMEM　MPAcc', '管理学-硕士学位论文-写作', '本书分为两部分共十一章，主要内容包括：寻找论题和阐明问题；研究设计；数据收集；数据分析；研究工作过程的质量检查和开题报告的撰写；论文构思；论文撰写技巧；序页及摘要写作等。', 2015, '李怀祖,田鹤亭,苗逎玲', '978-7-5624-9001-2', '', 'G642.477', '', '重庆大学出版社', '2019-01-04 17:58:25', '', NULL, '1093654', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1198', '1298', 'C93-53/X457c', '管理理论构建论文集', '管理学-文集', '本书囊括了西方已发表的多篇与理论构建有关的论文，通过传播已经显示巨大成功的西方学者的经验，致力于向中国管理学者介绍发展（构建）理论的哲学与方法。', 2016, '徐淑英,任兵,吕力', '978-7-301-27154-4', '', 'C93-53', '', '北京大学出版社', '2019-01-04 17:58:25', '', NULL, '1151241', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
