INSERT INTO `sys_book` VALUES ('1202', '1302', 'C93/K284e', '管理模型', '管理学-模型-英语-汉语-对照读物', '本书主要讲述了如何在工作中领导别人，如何在工作中激励他人，如何在工作中理解员工．如何高效工作。具体介绍的理论有：领导行为连续体理论,行动中心型领导方式，团队发展阶段模型、期望理论、激励理论,交流分析理论,NPL沟通模型、权力基础理论、包以德循环等。', 2015, '克莱顿,黑尔斯顿,国洪伟', '978-7-313-12358-9', '', 'C93', '', '上海交通大学出版社', '2019-01-04 17:58:26', '', NULL, '1048104', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1203', '1303', 'C93/Z233a', '管理实践纵论', '管理学-研究', '本书涉及内容广泛,涵盖人才观、时间观、职业观、创业观、文化观、管理观等方方面面,既有对“80、90后”知识群体有效管理的探索,又有对福建自贸试验区、城市群发展、海绵城市建设等案例的研析,体现了理论探索旨在学以致用,对当前经济建设与推进社会管理具有一定的借鉴作用。', 2016, '詹圣泽', '978-7-5605-9271-8', '', 'C93', '', '西安交通大学出版社', '2019-01-04 17:58:26', '', NULL, '1193009', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
