INSERT INTO `sys_book` VALUES ('1206', '1306', 'C93/N355', '管理书', '管理学', '本书涵盖了作为一名管理者在日常工作中最有可能面临的诸多问题，阐述了如何在各个管理领域使你和团队的绩效实现最大化，从而使你的管理达到预期效果。书中内容具体包括以下几个方面：管理者目标达成目标过程中的常见问题，对问题的概述及其含义关注这些问题的时间节点结果导向的行动一览表。', 2015, '牛顿,康青', '978-7-121-25971-5', '', 'C93', '', '电子工业出版社', '2019-01-04 17:58:26', '', NULL, '1069898', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
