INSERT INTO `sys_book` VALUES ('1208', '1308', 'C93/Z339', '管理思维跨界创新', '管理学', '本书根据作者长期的管理实践和研究,总结了管理者反应管理对象时运用的逻辑形式、结构和方法,结合实证性、科学性、系统性和创造性等要素,对管理思维的组成要素提出了新的观点;并运用管理创新思维模式,对产业经济、创新创业、互联网+、教育和健康管理等经济与社会发展的热点领域,通过撰写短文的方式进行“跨界”思考,', 2016, '赵征', '978-7-312-03914-0', '', 'C93', '', '中国科学技术大学出版社', '2019-01-04 17:58:26', '', NULL, '1121381', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
