INSERT INTO `sys_book` VALUES ('121', '221', 'TP393.081/W199', '恶意软件分析与检测', '计算机网络-安全技术', '伴随恶意代码种类和数量的不断增加,对恶意代码分析方法提出更高要求,在传统的特征码检测方法与启发式检测方法存在样本分析成本过高、无法有效检测未知恶意软件等问题;新型基于机器学习的恶意软件检测方法能够提高分析效率以及改善未知恶意软件检测性能,', 2017, '王俊峰', '978-7-03-051300-7', '', 'TP393.081', '', '科学出版社', '2019-01-04 17:57:49', '', NULL, '1202529', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
