INSERT INTO `sys_book` VALUES ('125', '225', 'null', '分布计算技术', '分布式计算机系统-研究', '本书重点介绍国防科技大学并行与分布计算课题组一个时期内在分布计算技术领域的研究成果，在某种意义上，也从一个视角反映该领域的技术发展。全书以课题组多年来培养的部分博士生毕业论文为基础，并进行必要的删减整编而成，内容既有网络、操作系统等传统方向，也涉及事务内存、量子计算等新兴方向。', 2018, '卢锡城', '978-7-03-055032-3', '', 'TP338.8', '', '科学出版社', '2019-01-04 17:57:49', '', NULL, '1249565', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
