INSERT INTO `sys_book` VALUES ('145', '245', 'TP393.06/L317', '关键基础设施网络的故障规律', '计算机网络-故障诊断', '本书围绕关键基础设施的故障规律这个核心问题，从三个方面介绍最新的研究成果，即故障的传播媒介（网络结构）、故障的传播内因（网络流量）、故障的传播行为（时空特点）。本书提出了网络故障预测和恢复的方法，为建立关键基础设施网络可靠性理论和方法提供支撑。', 2017, '李大庆', '978-7-121-31536-7', '', 'TP393.06', '', '电子工业出版社', '2019-01-04 17:57:50', '', NULL, '1220770', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
