INSERT INTO `sys_book` VALUES ('1487', '1587', 'C936/L531', '基于引导技术的工作坊设计', '组织管理学', '本书主要介绍如何通过两类工作坊将引导落地，并提供工作坊行进路线图、凌波五步、明星问题模型等独具特色的模型和工具。书中按工作坊设计的原理和要求来设计，让读者的阅读体验和互动体验更强烈。此外，全书涵盖引导前、引导中、引导后的工作开展，呈现真实的引导大画面和全过程，同时，将按照引导工作的准备流程循序渐进地展开内容。', 2017, '林士然', '978-7-121-31806-1', '', 'C936', '', '电子工业出版社', '2019-01-04 17:58:35', '', NULL, '1224897', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
