INSERT INTO `sys_book` VALUES ('1489', '1589', 'C936/M391', '基于“适配”的虚拟团队沟通管理研究', '虚拟公司-组织管理学', '本书第一部分系统介绍了虚拟团队的定义、发展、特征、分类等概述性内容；第二部分则是虚拟团队沟通管理的相关内容；第三部分则对信息系统学科中的“适配”理论进行综述；第四部分则是基于任务-技术-适配（TTF）视角下对跨国虚拟团队沟通管理进行研究等。', 2015, '闵庆飞,陈尚', '978-7-03-042581-2', '', 'C936', '', '科学出版社', '2019-01-04 17:58:35', '', NULL, '1035057', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
