INSERT INTO `sys_book` VALUES ('1504', '1604', 'null', '《文心雕龙》的骈文理论和实践', '文学理论－中国－南朝时代；《文心雕龙》－骈文－古典文学研究', '本书主体部分共分四章，从理论和实践两个方面考察《文心雕龙》在骈文理论和骈文创作上的成就和贡献。第一章是绪论，分为三节，着重从理论层面入手，阐述《文心雕龙》的骈文理论及其相关问题。第一节', 2017, '', '978-7-101-12251-0', '', 'I206.2', '', '中华书局', '2019-01-04 17:58:36', '', NULL, '1262192', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1505', '1605', 'null', '《文心雕龙》与《易》卦关系探微', '文学理论-中国', '本书以卦象为媒介，来探索《周易》和《文心雕龙》之间的内在隐秘关系。首先作者对《文心雕龙》文学超源论与《易》卦之间关系进行了考察，认为刘勰描述“道之文”的取象和八卦取象基本一致，“太极”是人文的逻辑起点，《易》象是人类的最早文化符号。其次，探讨了《文心雕龙》风格学体系与《易》卦的关系，认为八体、八性与八卦都可以建�', 2017, '李长庚', '978-7-01-018116-5', '', 'I206.2|B221.5', '', '人民出版社', '2019-01-04 17:58:36', '', NULL, '1236088', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1506', '1606', 'I207.414/J268', '《西遊記》之嘲諷架構研究', '古典小说-文学研究-中国-明代', '本书是一部作者对文学与历史的研究、是一本充满入世理想的历史论述。作者透过《西游记》一书并搭配明代后期历史，体察到当时民间对那一时代的看法与威想。本书是一位青年人的热血之作，它表达作者对世间的热爱，也表现出对人文学科的投入，同时这也属于一位抱持人文学科理想之青年人的生命记录。', 2016, '贾尚轩', 'null', '', 'I207.414', '', '文津出版社', '2019-01-04 17:58:36', '', NULL, '1258514', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1507', '1607', 'I207.6/D378', '《新刻對聯從新》考注', '对联-文学研究-中国-清代', '本书是把咸丰乙卯年及道光乙巳年两次刻印的《新刻对联从新》一书在编辑和刻印过程中出现的错误进行了系统的考证,对于楹联中出现的人名、不常用成语、典故、生僻字、休眠字等加入了注释,对现代已经不常用的汉字加入了汉语拼音。对中国传统文化的继承和发掘都具有十分重要的学术价值和现实意义。原书内容全面,', 2017, '东郡崇文轩主人,商立民', '978-7-5441-8454-0', '', 'I207.6', '', '沈阳出版社', '2019-01-04 17:58:36', '', NULL, '1235092', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1508', '1608', 'I207.419/X131d', '《醒世姻缘传》与明代世俗生活', '古典小说-文学研究-中国-清代,社会生活-研究-中国-明代', '本书以古代小说《醒世姻缘传》为研究对象,从制度文化、社会心理、日常习俗等多层面地反映了明代中后期的北方基层社会的世俗生活。', 2017, '吴晓龙', '978-7-100-14331-8', '', 'I207.419,D691.9', '', '商务印书馆', '2019-01-04 17:58:37', '', NULL, '1232217', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1509', '1609', 'I561.073/B221', '爱德华·邦德左翼戏剧的人性书写变奏', '邦德-戏剧文学-文学研究,戏剧文学-文学研究-英国-当代', '当代英国左翼剧作家爱德华·邦德的戏剧因惯用暴力书写而独具一格,他的戏剧具有情节的暴力性、内涵的悲剧性和主题上的人性反思诉求。本书以对邦德作品的历时研究为基本方略,意在探究邦德戏剧中人性书写思想的变化过程和意义所在。在厘定邦德人性观的基础上,笔者通过以《战争三部曲》为界,', 2017, '夏延华', '978-7-03-054299-1', '', 'I561.073', '', '科学出版社', '2019-01-04 17:58:37', '', NULL, '1231101', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
