INSERT INTO `sys_book` VALUES ('1511', '1611', 'null', '爱上写作', '文学写作学', '本书选择简书中关注度比较高的作者，把他们创作的关于如果提高写作技能、如何获得关注度的写作经验指导方面的文章进行一个整合，方便对写作感兴趣的读者的学习。本书选题的初衷，是为迎合目前自媒体写作的大的趋势，同时为热衷自媒体写作的入门者提供一个学习的方法和一种榜样的力量。', 2017, '', '978-7-5131-3736-2', '', 'I04', '', '开明出版社', '2019-01-04 17:58:37', '', NULL, '1261368', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
