INSERT INTO `sys_book` VALUES ('1517', '1617', 'null', '白蛇传的现代诠释', '中国文学-文学研究', '本书采用互文性理论和谅社会学方法，以文本本细读的方式，整合、分析了近百部白蛇传作品，提取其中具有艺术审美价值和学术理论意义的问题。白蛇传的改写要有现代意识，改写的的主题和流露的思想、情感，要符合现代人的审美观和价值观，符合人类文明的现代进程。', 2017, '李斌', '978-7-5203-1148-9', '', 'I206', '', '中国社会科学出版社', '2019-01-04 17:58:37', '', NULL, '1250753', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1518', '1618', 'null', '百家点评人间词话', '词(文学)-诗词研究-中国', '本书分四卷，各有卷首总批，对于该卷的来历及要义作整体评判。全编一五六则，每则内容，由原文、别叙、集评三个部分组成。“别叙”部分带有导读性质，主要针对《人间词话》词条原文及百家点评发表个人意见，可当作题解看，而其评说对象则主要是集评所采摘。', 2017, '邓菀莛', '978-7-5325-8650-9', '', 'I207.23', '', '上海古籍出版社', '2019-01-04 17:58:37', '', NULL, '1257558', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1519', '1619', 'null', '百年风雅', '传记文学-中国', '本书选取陈寅恪、黄炎培、张君劢、费孝通、钱玄同、邓稼先、朱希祖、胡适等八个江南名门望族，讲述百年中国的家国变迁。文化世家的如烟往事，在历史风云中变幻无常。一家三代的历史命运，与国运息息相通。', 2018, '刘宜庆', '978-7-5594-0206-6', '', 'I25', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:37', '', NULL, '1257092', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
