INSERT INTO `sys_book` VALUES ('1544', '1644', 'null', '曹芳曹髦曹奂集校注', '建安文学-古典文学研究', '本书对三国曹芳、曹髦、曹奂的诗、赋、论、诏、文书进行校注，每篇诗、赋、论、诏、文书皆分为解题、正文、异文、校注四部分。正文是该篇诗、赋、论、诏、文书的内容。异文若历代文献中转引内容与底本存在较大差异，则在此部分中详细胪列。解题系对该篇诗、赋、论、诏、文书的撰写或颁布的时间、主要内容、历代文献收录情况、校对的底本�', 2017, '曹芳,曹髦,曹奂,郭善兵', '978-7-5690-0456-4', '', 'I209.342', '', '四川大学出版社', '2019-01-04 17:58:38', '', NULL, '1230768', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1545', '1645', 'null', '曹魏三祖时期文学研究', '古典文学-文学研究-中国', '本书在以现存汉末曹魏时期的文史资料为基础,将“魏之三祖”(太祖曹操、世祖曹丕、烈祖曹叡)时期的文学与史料在相互印证和补充下,努力还原最为接近历史真实的文学研究。本书的研究从宏观上依据三祖的时代特色和文学发展的时代性规律来划分研究的层次阶段:建安十三年为界将曹操时期的文学分成前后两个阶段,', 2017, '张丽锋', '978-7-5201-1222-2', '', 'I206.2', '', '社会科学文献出版社', '2019-01-04 17:58:38', '', NULL, '1235607', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
