INSERT INTO `sys_book` VALUES ('155', '255', 'TP273/B533', '黑客大曝光:工业控制系统安全', '工业控制系统-计算机网络-网络安全', '本书共分三个部分：第一部分包括第1～3章，主要对工控系统的架构与组成、工控系统的风险评估以及工控系统的威胁情报进行介绍。第二部分由第4～8章组成，主要对工控系统渗透测试、协议、设备、应用以及针对工控系统的“0-day”漏洞挖掘与恶意代码进行介绍。第三部分包括第9、10章，对工控系统安全标准及风险缓解策略进行介绍。', 2017, '博顿金,辛格,施比卜,戴超,张鹿,王圆', '978-7-111-57594-8', '', 'TP273,TP393.08', '', '机械工业出版社', '2019-01-04 17:57:50', '', NULL, '1237239', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1550', '1650', 'null', '查慎行全集', '中国文学－古典文学－作品综合集－清代', '查慎行(1650-1727),清代著名诗人、学者,初名嗣琏,字夏重,号查田,后改名慎行,字悔余,号他山、烟波钓徒,晚年居初白庵,称查初白,浙江海宁人。康熙四十二年(1703)进士,特授翰林院编修,入直内廷。五十二年(1713)乞休归里。查慎行诗学东坡、放翁,受经史于著名学者黄宗羲,受诗法于桐城诗人钱澄之;与朱彝尊为中表兄弟,得其奖誉,', 2017, '查慎行,范道济', '978-7-101-12214-5', '', 'I214.92', '', '中华书局', '2019-01-04 17:58:38', '', NULL, '1255896', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
