INSERT INTO `sys_book` VALUES ('1636', '1736', 'null', '都市魔方:广州都市文学与都市文化研究', '都市文学-文学研究-广州', '本书共分为都市文学；都市文化与传播两辑，其主要内容包括：新世纪都市文学的发展轨迹；张欣：掘金时代不安灵魂的抚慰者；张梅的广州：非虚构的城市记忆；黄爱东西及其广州女性都市散文等。', 2017, '江冰', '978-7-5360-8493-3', '', 'I209.965', '', '花城出版社', '2019-01-04 17:58:41', '', NULL, '1247634', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
