INSERT INTO `sys_book` VALUES ('1638', '1738', 'null', '读集丛考', '中国文学－古典文学研究', '本书为作者近几年来研读集部文学文献的思考所得,36篇文章有的已经发表,有的首次公开,一类涉及阮籍、鲍照、陶渊明、高启、黄虞稷等历代作者的著述版本、诗文系年、佚文辑考及基于新文献的细读考评;另一类涉及具体著作的考证,如《光岳英华》、《乐府古题要解》、咏史乐府三十八种等,均能在已有研究基础上推进一步。', 2017, '颜庆余', '978-7-5506-2687-4', '', 'I206.2', '', '凤凰出版社', '2019-01-04 17:58:42', '', NULL, '1255742', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
