INSERT INTO `sys_book` VALUES ('1669', '1769', 'null', '纷繁世界的背影:诺贝尔文学主题学漫议', '文学评论－世界', '本书稿对历年来获诺贝尔文学奖的大师名作进行了梳理,按照不同的主题加以分类,并进行了较为详细的分析。作者认为,深入剖析诺贝尔文学奖获奖作品的主题,有助于普通读者了解获奖作品的创作背景和人文价值,更好地欣赏其文学魅力。本书为中山市社会科学联合会编写的“中山市社科普及丛书”之一种。该丛书旨在用通俗易懂的语言,', 2017, '郑万里', '978-7-218-11647-1', '', 'I106', '', '广东人民出版社', '2019-01-04 17:58:42', '', NULL, '1255331', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('167', '267', 'TP309.5/Z264', '基于监督学习的病毒检测技术研究', '计算机病毒-检测-研究', '本书主要介绍基于有监督机器学习理论指导下的病毒自动化和智能化检测技术。内容分为三个部分，第一部分研究以API函数调用信息作为病毒行为特征的动态检测技术。第二部分研究以从程序二进制比特流中抽取的n-gram信息作为病毒特征的静态检测技术。第三部分是病毒动态检测与静态检测方法相结合的集成检测技术研究。', 2017, '张波云', '978-7-307-19904-0', '', 'TP309.5', '', '武汉大学出版社', '2019-01-04 17:57:51', '', NULL, '1258132', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
