INSERT INTO `sys_book` VALUES ('1672', '1772', 'null', '风向与信仰:金佛庄烈士传', '纪实文学,烈士-传记-中国,中国', '本书以纪实的手法，通过生动的故事，佐以大量的历史资料，展现了金佛庄烈士立志救国、保定求学、坚定信仰、才华初显、黄埔立业、东征建功、北伐征战、壮志未酬、以身许国的光辉历程。', 2017, '李新勇', '978-7-5399-9435-2', '', 'I253|K827=6', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:43', '', NULL, '1243177', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1673', '1773', 'null', '风烟望故国', '词(文学)－作品集－中国－当代', '《风烟望故国》是作者的一部词集，重点抒写以下几方面内容：一是家国之思，二是军人壮怀，三是人伦深情，四是闲适雅趣。这既表明了作者精神世界之丰富，也说明他词作的题材范围之宽广。他追循的是词的主流，却又不拘泥于某些论说，可以说是转益多师，博采众长，形成了自己的艺术天地。', 2017, '', '978-7-02-012499-2', '', 'I227.8', '', '人民文学出版社', '2019-01-04 17:58:43', '', NULL, '1242250', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
