INSERT INTO `sys_book` VALUES ('1675', '1775', 'null', '复活的财神:沈万三大传', '传记文学-中国', '本书主要讲述了沈万三如何由一介布衣而成长为天下首富的辉煌传奇。沈万三出海贸易,通番经商而获得第一桶金,展示了令人惊叹的商业才能;又以卓越的商业品格赢得了陆道源的信任,将女儿嫁给他并送上了丰厚的嫁妆--充满传奇色彩的“聚宝盆”,帮助沈万三一跃成为江南首富。致富后的沈万三因资助张士诚兄弟经营天下,', 2017, '安之忠', '978-7-5090-1254-3', '', 'I25', '', '当代世界出版社', '2019-01-04 17:58:43', '', NULL, '1232168', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
