INSERT INTO `sys_book` VALUES ('1677', '1777', 'null', '符号·翻译·英诗', '英语诗歌－文学翻译－研究', '社会符号学语义功能翻译模式在韩礼德社会符号学语言模式的大框架内、在巴尔胡达罗夫语义符号学翻译模式基础上，融入系统功能语法的三大纯理功能，提出立体扫描式的社会符号学语义功能翻译模式，探讨意义、功能、情景语境的翻译分类及方法。语篇分析与英诗将语篇分析理论应用到英诗的分析中，使英诗的研究更加科学、更加精细、更加准确。�', 2017, '', '978-7-5668-2157-7', '', 'H315.9', '', '暨南大学出版社', '2019-01-04 17:58:43', '', NULL, '1242788', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
