INSERT INTO `sys_book` VALUES ('168', '268', 'null', '基于流记录的网络流量识别关键技术研究', '计算机网络-流量,识别,研究', '本书作者基于机器学习的识别方法，基于流量的特征属性的影响以及测度之间的相关性，通过建立标准数据集，研究报文抽样、属性选择算法对识别精度的影响，评估分类算法对属性特征在抽样条件下的敏感程度，在此基础上，建立了面向不同环境和需求的多分类器融合流量识别模型。', 2017, '董仕', '978-7-5189-2977-1', '', 'TP393', '', '科学技术文献出版社', '2019-01-04 17:57:51', '', NULL, '1237086', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1680', '1780', 'null', '皋轩文编', '中国文学－古典文学－作品综合集－清代', '《皋轩文编》十卷。作者李光坡，李光坡为清初名臣李光地胞弟，福建泉州经学家，专研《三礼》，旁及他经，是清初闽中学派代表人物之一。《皋轩文编》十卷为李光坡文集，内容包括其经学研究文章、序跋、信札、及所作寿文、墓志、碑铭、祭文，以及诗作等丰富内容。《杂著》收录李光坡理学论撰十九篇，主要内容多为讨论性理、周易、五行学说�', 2017, '', '978-7-100-12753-0', '', 'I214.92', '', '商务印书馆', '2019-01-04 17:58:43', '', NULL, '1242261', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1681', '1781', 'I217.62/L767c', '个中语调', '中国文学-当代文学-作品集', '本书是作者在积累多年教学经验的基础上，收集评论文字、课堂文本细读、演讲稿、访谈等，整理成的一本文学札记。兼具活泼有趣的随笔风格和思辩的学术价值。既有在大学课堂上讲解的现场感，又有对于诗人群体和诗歌文本的见解。内容包括：宇宙主义者之诗；诗人中的“达吉雅娜”；甘肃的反义词；诗人的语调和意图等。', 2017, '路也', '978-7-5378-5247-0', '', 'I217.62', '', '北岳文艺出版社', '2019-01-04 17:58:43', '', NULL, '1229858', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1682', '1782', 'I470.065/K181', '哥伦比亚南非英语文学导读(1945-)', '英语文学-文学研究-南非-现代', '本书是一本译著。原书是几位国际著名学者在参考大量学术和历史文献的基础上,描绘20世纪南非文学概貌的鼎力之作。按照从A到Z的字母顺序,列出了南非主要作家的生平、代表作、对南非文坛的贡献等,是研究南非文学必不可少的著作。', 2017, '康维尔,克劳普,麦克肯基,蔡圣勤', '978-7-307-19350-5', '', 'I470.065', '', '武汉大学出版社', '2019-01-04 17:58:43', '', NULL, '1236446', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1683', '1783', 'null', '歌词创作与中国现当代文化运作', '歌词,歌词-文学创作研究,文学创作研究-中国,中国', '本书从中国现当代歌词参与精英文化、政治文化和娱乐文化运作入手，对中国现当代歌词创作进行历史梳理，关注中国现当代歌词创作的各种文化现象。通过对影响中国现当代歌词创作的社会因素和文化因素的考察与分析，探讨歌词创作如何成为中国现当代文化运作的先锋。', 2017, '张明明', '978-7-5686-0111-5', '', 'I207.22', '', '黑龙江大学出版社', '2019-01-04 17:58:43', '', NULL, '1257363', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
