INSERT INTO `sys_book` VALUES ('1745', '1845', 'null', '基于马克思及其文化的文学研究', '马克思主义-文学理论,研究', '本书从文学文化学的比较视域，聚焦于马克思及其文化，以期对文学理论做出系统研究。本书全然是跨学科的研究。作者在研究中探究了一系列的话题，适用于文学的问题；对由意识形态所引发的马克思主义者与基督徒之间的纷争，作者重构了文学研究，目的在于应对此瓶颈问题，并探究了文学与审美，以期突破阻碍。', 2017, '李志雄', '978-7-5649-3179-7', '', 'A811.691', '', '河南大学出版社', '2019-01-04 17:58:45', '', NULL, '1264255', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
