INSERT INTO `sys_book` VALUES ('1751', '1851', 'null', '记忆的颜色', '纪实文学-作品集-中国', '本书包括《胶片的温度》《书斋外的学者》《记忆的颜色》和《喧嚣背后的角落》四篇纪实文学，分别讲述了作者作为电影导演，在眼下这个数字时代对电影胶片的若干回忆，对老电影人和文化名人晚年生活的记录，以及对按摩师这种相对特殊职业的客观描述。', 2018, '彭小莲', '978-7-5594-1027-6', '', 'I25', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:45', '', NULL, '1249086', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1752', '1852', 'null', '加里·斯奈德与禅宗文化', '加里·斯奈德－文学研究－汉、英；加里·斯奈德－禅宗－思想评论－汉、英', '《加里·斯奈德与禅宗文化》一书深入探掘美国深层生态诗人加里·斯奈德与东方禅宗文化的关联性。其中包括探讨斯奈德痴迷于禅宗文化的深层文化动因，及他的坐禅修行对其思想理念的影响，在文学作品中的体现。本书尤其重点探寻斯奈德如何将禅宗思想理念揉入其多元化的文化立场，提出了“重新定居”的深层生态栖居的主张。', 2017, '', '978-7-5135-9590-2', '', 'I712.065；B946.5', '', '外语教学与研究出版社', '2019-01-04 17:58:45', '', NULL, '1250367', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
