INSERT INTO `sys_book` VALUES ('1754', '1854', 'I252/L222', '家族往事', '纪实文学-中国-现代-作品集', '本书由老鬼、马勇、萧功秦、俞可平等17位作者执笔,围绕“家人、家庭、亲情”主题,撰写各自的家族往事。17个故事,17个家族的兴衰跌宕,逐帧刻画家人平凡而坎坷的人生经历、辛酸苦乐。其中包括对抗战历史的个人描述、对父母人生经历的回顾、对故乡亲族的追忆等。作者文笔流畅,娓娓道来,故事真实感人,', 2017, '老鬼,马勇,萧功秦', '978-7-80768-196-0', '', 'I252', '', '三联书店:生活书店出版有限公司', '2019-01-04 17:58:45', '', NULL, '1240034', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1755', '1855', 'null', '贾似道及其文学交游研究', '贾似道(1213-1275)－文学研究', '本书志在厘清宋季权臣贾似道及其与周边文人的交游，在误国权奸的习见判词之外展现出更加丰富而真实的历史细节，还原风雨飘摇的宋季政局下权臣与文人们具体而微的历史时空，由此为宋季文学文化研究带来重新评估的可能。全书分为上下两篇共计六章。上篇“贾似道的实相与虚构”三章旨在梳理贾似道其人的历史功过、政治举措之于四学及其形象�', 2017, '', '978-7-5403-4198-5', '', 'I206.2', '', '崇文书局', '2019-01-04 17:58:45', '', NULL, '1261684', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
