INSERT INTO `sys_book` VALUES ('1767', '1867', 'null', '今夜，不要每况愈下', '传记文学-美国', '本书中亚历山德拉·富勒回忆了那段在非洲混乱却又美好的童年生活。通过那些时而滑稽可笑，时而悲伤不已的文字，她讲述了自己在童年所经历的战争，及一家人在非洲感受到的爱与痛。', 2018, '富勒,豆浩亮', '978-7-5598-0548-5', '', 'I712.55', '', '广西师范大学出版社', '2019-01-04 17:58:45', '', NULL, '1264291', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
