INSERT INTO `sys_book` VALUES ('1769', '1869', 'null', '近代报刊诗话研究:1870—1919', '诗话－文学研究－中国－1870-1919', '本稿以近代报刊诗话为研究对象，重点考察其在1870—1919年间产生、发展和嬗变的诗学批评生态，还原报刊诗话的创作风貌，探讨该时期报刊文学的发展和演变轨迹，拓展近代诗学批评的研究领域。本稿对近代与民国诗话的“转型”问题予以特别的关注，在中西融合的历史潮流中，它生成于近代传媒，产生了新变，但也与传统诗学有着密切的联系。其思想', 2017, '', '978-7-5458-1563-4', '', 'I207.22', '', '上海书店出版社', '2019-01-04 17:58:45', '', NULL, '1262266', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
