INSERT INTO `sys_book` VALUES ('1770', '1870', 'I212.01/Z624/(9)', '近代诗文', '中国文学-古典文学-作品综合集,古典诗歌-赏析-中国-近代,古典散文-赏析-中国-近代', '本书选取我国近代诗文词名家名作，进行校勘、注释，并汇历代名家辩析、批评于一炉。本书分为诗选、词选、文选三部分，诗选部分以龚自珍、郑珍、黄遵宪和陈三立四家为多，词选部分以项鸿祚、蒋春霖、王鹏运等七家居多，古文所重，仅曾国藩、林杼、梁启超三家。', 2017, '黄明,黄坤', '978-7-208-14673-0', '', 'I207.227.5,I207.65', '', '上海人民出版社', '2019-01-04 17:58:45', '', NULL, '1230601', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
