INSERT INTO `sys_book` VALUES ('1774', '1874', 'null', '惊鸿照影:唐琬', '传记文学-中国', '本书以南宋深受广大文学爱好者喜闻乐道的书香才女唐琬为主人公，刻画了她温婉清愁的闺阁女子性格，展现了她执着坚强、博文广识的品质，在走近这位史书上着墨不多的才女之余，也可以借此卷文字了解南宋时代的生活琐细。', 2018, '张晓慧', '978-7-5656-3704-9', '', 'I25', '', '首都师范大学出版社', '2019-01-04 17:58:46', '', NULL, '1265055', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1775', '1875', 'null', '惊天动地的“两弹”元勋', '报告文学-中国', '这是一部描写我国“两弹一星”元勋王淦昌的报告文学。作者以大量翔实的资料和生动的文笔,详细记叙了世界著名的核物理学家、中国核科学与实验原子核物理的奠基人和开拓者王淦昌默默负重、妙趣横生而又惊天动地的一生。书中通过大量感人故事,歌颂了这位高尚品格、一生三次与诺贝尔奖擦肩而过,', 2018, '何建明', '978-7-5364-8924-0', '', 'I25', '', '四川科学技术出版社', '2019-01-04 17:58:46', '', NULL, '1256089', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
