INSERT INTO `sys_book` VALUES ('1787', '1887', 'null', '科尔沁蒙古族说唱文学研究', '蒙古族-说唱文学-中国', '蒙古族文学是一种多元文化的融合,其说唱文学就是多元文化的具体展示;科尔沁蒙古族说唱文学和一般文学一样,通过审美功能,实现其认识功能和教育功能。说唱文学具有明显的原生态质素,因此对科尔沁蒙古族说唱文学进行文化人类学研究,', 2017, '何红艳', '978-7-5668-2231-4', '', 'I207.39', '', '暨南大学出版社', '2019-01-04 17:58:46', '', NULL, '1255966', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
