INSERT INTO `sys_book` VALUES ('1848', '1948', 'null', '爱情与阴影', '长篇小说－智利－现代', '《爱情与阴影》的故事中,女孩埃万赫利娜患有一种罕见癫痫,发作时房舍摇晃,餐具飞舞,家禽异常。她因此被警察污蔑为“妖魔作怪”,下落不明。女记者伊雷内与摄影师弗朗西斯科出于对受压迫者的同情四处奔走,于废井发现了女孩的尸体,此外竟还有几十具无名残躯。自此,军方暴行被披露报端,', 2018, '阿连德,陈凯先', '978-7-5447-5234-3', '', 'I784.45', '', '译林出版社', '2019-01-04 17:58:48', '', NULL, '1255917', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
