INSERT INTO `sys_book` VALUES ('185', '285', 'TP393.07/C227', '技术创新网络惯例、关系机制与治理目标', '计算机网络管理-研究', '本书结合技术创新网络组织间互动特征，从机制层面理论分析并实证检验了网络惯例对技术创新网络治理目标的作用机理，并得出相关结论，其主要内容包括：文献综述；理论模型与研究假设等。', 2017, '常红锦', '978-7-5096-5164-3', '', 'TP393.07', '', '经济管理出版社', '2019-01-04 17:57:51', '', NULL, '1220325', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1850', '1950', 'I313.45/S224n2', '艾比斯之梦', '短篇小说-小说集-日本-现代', '本书主要内容包括:宇宙尽在我指尖、令人雀跃的虚拟空间、镜中女孩、黑洞潜者、正义不打折的世界、诗音翩然到来之日等。', 2015, '山本弘,张智渊', '978-7-5133-1763-4', '', 'I313.45', '', '新星出版社', '2019-01-04 17:58:48', '', NULL, '1261296', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
