INSERT INTO `sys_book` VALUES ('1853', '1953', 'I532.45/H175', '安德烈·托尔斯泰', '传记小说-瑞典-现代,长篇小说-瑞典-现代', '《安德烈·托尔斯泰》是关于托尔斯泰家庭的长篇小说,作者主人公安德烈托尔斯泰是大文豪托尔斯泰的第九个孩子,作者通过描写安德烈从军、参战,到回乡、去世及在此期间一系列的心理历程,揭开主人公安德烈与父亲托尔斯泰及母亲索菲亚温情又有矛盾的关系,给读者提供一个观察托尔斯泰和他的家庭生活的独特视角。', 2017, '汉松,李之义', '978-7-02-011698-0', '', 'I532.45', '', '人民文学出版社', '2019-01-04 17:58:48', '', NULL, '1229525', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
