INSERT INTO `sys_book` VALUES ('1859', '1959', 'I712.45/X157f', '暗杀大师,秘密仆人', '长篇小说-美国-现代', '本书讲述英国伦敦，四枚炸弹在市中心爆炸，数百人因此丧生，整个城市血流成河，满目疮痍。传奇特工加百列·艾隆赶赴现场，却没能阻止这一切。他一路追查，却不偏不倚地落入了敌人设下的陷阱，几乎丧命。死里逃生的他并不知道，自己才是这群亡命之徒的真正目标。', 2017, '席尔瓦,万丽莹,陈磊', '978-7-5594-0227-1', '', 'I712.45', '', '江苏凤凰文艺出版社', '2019-01-04 17:58:48', '', NULL, '1230672', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
