INSERT INTO `sys_book` VALUES ('1870', '1970', 'null', '白老虎', '长篇小说－小说集－中国－当代', '本书收录当代原创长篇小说三部。《震惊》讲述了唐山大地震后曾经发生地震的鲁南地区陷入一片恐慌，一对兄妹梦寐以求的目标因此破灭。这部小说让我们对自然生命以及人生种种命题的不作出深沉而严肃的探究和思考。《魔戒之旅》讲述了一群电影《魔戒》的发烧友参加新西兰的“魔戒之旅”的经历，展现了东西方文化和各种观念的冲突和融合。《�', 2017, '', '978-7-5396-6250-3', '', 'I247.5', '', '安徽文艺出版社', '2019-01-04 17:58:48', '', NULL, '1261325', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
INSERT INTO `sys_book` VALUES ('1871', '1971', 'null', '白马山庄谜案', '长篇小说－日本－现代', '本书为东野圭吾长篇小说。两个女孩为了探查多年前的一桩旧案，来到长野县的白马山庄，遇见了和当年案发时几乎一模一样的一群客人。八个房间，每个房间里都有一首鹅妈妈童谣。两个女孩逐一探访八个房间，接触八位客人，揣摩八首童谣，最终找出了埋藏着所谓宝藏的地方，对童谣的解释也就成为阅读这篇小说的最大乐趣之一。', 2011, '', '978-7-02-013488-5', '', 'I313.45', '', '人民文学出版社', '2019-01-04 17:58:49', '', NULL, '1261704', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
