INSERT INTO `sys_book` VALUES ('1876', '1976', 'I207.42-53/Y242', '百年中国科幻小说精品赏析', '科学幻想小说-小说研究-中国-现代,科学幻想小说-小说研究-中国-当代', '本书为中国科普研究所的资助项目。项目首先对中国百年历史长河中的科幻作品进行梳理,从中遴选55部中国原创科幻小说精品,然后在细读文本的基础上对入选作家作品进行深度赏析。研究突出史诗性,即以百年历史为线索编选作家作品;强调专题性,即对各个时代科幻作家的代表性作品进行赏析,凸显其创作特色和文学风格,', 2017, '姚义贤,王卫英', '978-7-110-09462-4', '', 'I207.42-53', '', '科学普及出版社', '2019-01-04 17:58:49', '', NULL, '1239556', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
