INSERT INTO `sys_book` VALUES ('1881', '1981', 'null', '暴力夺取', '长篇小说－美国－现代', '《暴力夺取》是奥康纳的代表作。父母双亡的男孩塔沃特在婴儿时就被狂热的基督教徒舅爷爷偷走，在荒林里当作先知的接班人培养。当老人死后，他回到城中舅舅家，努力在否定自己先知的命运，却被内心不知名的力量左右。《暴力夺取》是奥康纳的一部重要作品。', 2017, '', '978-7-02-012910-2', '', 'I712.45', '', '人民文学出版社', '2019-01-04 17:58:49', '', NULL, '1242155', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
