INSERT INTO `sys_book` VALUES ('1890', '1990', 'null', '贝尔曼与黑衣人', '长篇小说-英国', '本书讲述了：在威廉·贝尔曼过了十岁生日后的第四天，他用弹弓射杀了一只风鸦，很快他也忘记了这件事。成年后，他接管了自己叔叔的工厂，拥有了一个完美的家庭以及一份成功的事业。直到悲剧发生，他的家人一个个死去，在葬礼上，穿着黑衣的陌生人频频出现，在最绝望的时刻，他和黑衣人做了一个交易，想挽救他唯一剩下的女儿。在之后，威廉', 2017, '赛特菲尔德,马丹', '978-7-02-013104-4', '', 'I561.44', '', '人民文学出版社', '2019-01-04 17:58:49', '', NULL, '1236138', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, '2019-01-08 12:31:41');
