INSERT INTO `sys_book` VALUES ('1919', '2019', 'I313.45/D378an', '沉睡的森林', '长篇小说-侦探小说-日本-现代', '本书是一部长篇小说，描述了1983年12月24日，福岛县御仓市议员一家人被惨杀。父母当场死亡，大量出血的长女最终在送医院途中不治身亡，12岁的次女是这事件中唯一的生存者。第一个发现的是名大学生，被杀长女的男朋友。三天后，这名大学生被怀疑是杀人凶手，从而被捕。', 2012, '东野圭吾,郑琳', '978-7-5442-5698-8', '', 'I313.45', '', '南海出版公司', '2019-01-04 17:58:51', '', NULL, '1251190', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
