INSERT INTO `sys_book` VALUES ('1929', '2029', 'null', '出征马来亚', '长篇小说-法国', '本书讲述了：夏尔·蓬迪亚克和让－弗朗索瓦·蓬斯都钟情于一个叫尼科尔·费希尔的女人，而她却更喜爱坐收渔利的第三者，而且还在将一个长着金发的、粉红脸蛋的小朱斯迪娜降生到世上之前就成了寡妇。她忧伤的、被弃的情人们决定远赴天涯。一个在马来亚（怎样去更好地表达他的苦恼？），另一个游荡在人行道上和巴黎的桥下。生活将把已生皱纹�', 2017, '艾什诺兹,赵家鹤', '978-7-5404-8370-8', '', 'I565.45', '', '湖南文艺出版社', '2019-01-04 17:58:52', '', NULL, '1261047', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
