INSERT INTO `sys_book` VALUES ('1944', '2044', 'I207.42/Y197', '从革命女性到女性革命:现代女性革命小说的话语流变', '妇女文学-小说研究-中国-现代', '中国女性解放与民族国家建构、左翼革命的关系构成了现代以来中国文学与文化的焦点问题之一。本书以1898～1949年为历史跨度,以中国现代革命女作家的创作为研究对象,全面梳理现代女性革命话语的变化历程,从宏观把握和个案分析并重的角度论述“性别政治”在不同的社会历史和政治时空而呈现的不同表征与特点,', 2017, '杨晶', '978-7-5203-0078-0', '', 'I207.42', '', '中国社会科学出版社', '2019-01-04 17:58:52', '', NULL, '1225819', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
