INSERT INTO `sys_book` VALUES ('1946', '2046', 'I313.45/D378aq', '从前我死去的家', '长篇小说-日本-现代,推理小说-日本-现代', '本书讲述沙也加对小学以前的童年毫无印象。为找回记忆，她和前男友来到父亲生前常独自前往的、神秘荒凉的别墅。种种迹象表明，这幢别墅的的人似乎在二十三年前的某一天全部消失，再也没有回来。当看到一架钢琴的时候，沙也加禁不住喃喃自语：从前我来过这里。', 2013, '东野圭吾,李盈春', '978-7-5442-6506-5', '', 'I313.45', '', '南海出版公司', '2019-01-04 17:58:52', '', NULL, '1251192', '1f26a53ba86746969d54f42093ed6249', NULL, 1, NULL, NULL, 0, NULL);
